import React, { useState, useRef } from "react";
import "./App.css";

function App() {
  const [activeTab, setActiveTab] = useState(0);
  const tabsContainer = useRef(null);

  const tabs = [
    { id: "lun", label: "Lunedì", content: "Matematica, Italiano, Scienze." },
    { id: "mar", label: "Martedì", content: "Storia, Geografia, Arte." },
    { id: "mer", label: "Mercoledì", content: "Educazione fisica, Inglese, Informatica." },
    { id: "gio", label: "Giovedì", content: "Chimica, Fisica, Musica." },
    { id: "ven", label: "Venerdì", content: "Letteratura, Disegno tecnico, Filosofia." },
  ];

  const fillPercent = tabs.length > 1 ? (activeTab / (tabs.length - 1)) * 100 : 0;

  const scrollTabs = (direction) => {
    if (tabsContainer.current) {
      const scrollAmount = 120; // px da scorrere
      tabsContainer.current.scrollBy({
        left: direction === "left" ? -scrollAmount : scrollAmount,
        behavior: "smooth",
      });
    }
  };

  return (
    <div className="App">
      <div className="timetable-slider">
        {/* <div className="line" /> */}
        <div className="line" style={{ "--fill": `${(activeTab / (tabs.length - 1)) * 100}%` }}></div>


        {/* <button className="scroll-btn left" onClick={() => scrollTabs("left")}>
          &#10094;
        </button>
        <button className="scroll-btn right" onClick={() => scrollTabs("right")}>
          &#10095;
        </button> */}

        <ul className="tabs" ref={tabsContainer}>
          {tabs.map((tab, idx) => (
            <li
              key={tab.id}
              className={activeTab === idx ? "active" : ""}
              onClick={() => setActiveTab(idx)}
            >
              <span className="dot" />
              <span className="label">{tab.label}</span>
            </li>
          ))}
        </ul>

        <div className="tab-content">
          <h2>Programma di {tabs[activeTab].label}</h2>
          <p>{tabs[activeTab].content}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
