import React, { useState } from "react";
import emailjs from "@emailjs/browser";

function FormConferma() {
  const [formData, setFormData] = useState({
    name: "",
    conferma: false,
    guests: "",
    children: "",
    childrenAge: "",
    diet: "",
    night: false,
    msg: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleChange = (e) => {
    const { name, type, value, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Esempio di validazione minima
    if (!formData.name) {
      setError("Nome e cognome sono obbligatori!");
      setSuccess("");
      return;
    }

    setError("");

    const serviceID = "service_rxwq3ft";
    const templateID = "template_tqq6v3j";
    const publicKey = "_rofz9XCjKw5a1jZ8";

    emailjs.send(serviceID, templateID, formData, publicKey).then(
      () => {
        setSuccess("Email inviata correttamente!");
        setFormData({
          name: "",
          conferma: false,
          guests: "",
          children: "",
          childrenAge: "",
          diet: "",
          night: false,
          msg: "",
        });
      },
      () => setError("Errore nell'invio dell'email!")
    );
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}

      <label>
        Chi sei tu, meraviglioso invitato? Nome e cognome:
        <input
          type="text"
          name="name"
          placeholder="Così possiamo scrivere bene sul segnaposto e non chiamarti “quello simpatico con gli occhiali”. "
          value={formData.name}
          onChange={handleChange}
        />
      </label>

      <label>
        Confermi la tua presenza al Matrimonio?
        <input
          type="checkbox"
          name="conferma"
          checked={formData.conferma}
          onChange={handleChange}
        />
      </label>

      <input
        type="text"
        name="guests"
        placeholder="Nomi persone aggiuntive (maggiorenni)"
        value={formData.guests}
        onChange={handleChange}
      />
      <input
        type="number"
        name="children"
        placeholder="Numero di bambini"
        value={formData.children}
        onChange={handleChange}
      />
      <input
        type="text"
        name="childrenAge"
        placeholder="Età dei bambini"
        value={formData.childrenAge}
        onChange={handleChange}
      />
      <input
        type="text"
        name="diet"
        placeholder="Allergie o intolleranze alimentari"
        value={formData.diet}
        onChange={handleChange}
      />

      <label>
        Sistemazione notte:
        <input
          type="checkbox"
          name="night"
          checked={formData.night}
          onChange={handleChange}
        />
      </label>

      <textarea
        name="msg"
        placeholder="Messaggio"
        value={formData.msg}
        onChange={handleChange}
      />

      <button type="submit">Invia</button>
    </form>
  );
}

export default FormConferma;
