import React, { useState, useRef, useEffect } from "react";
import "./App.css";

function App() {
  const [activeTab, setActiveTab] = useState(0);
  const tabsContainer = useRef(null);
  const lineRef = useRef(null);

  const tabs = [
    { id: "lun", label: "Lunedì", content: "Matematica, Italiano, Scienze." },
    { id: "mar", label: "Martedì", content: "Storia, Geografia, Arte." },
    { id: "mer", label: "Mercoledì", content: "Educazione fisica, Inglese, Informatica." },
    { id: "gio", label: "Giovedì", content: "Chimica, Fisica, Musica." },
    { id: "ven", label: "Venerdì", content: "Letteratura, Disegno tecnico, Filosofia." },
  ];

  useEffect(() => {
    // Aggiorna larghezza linea viola
    if (lineRef.current) {
      const fillPercent = tabs.length > 1 ? (activeTab / (tabs.length - 1)) * 100 : 0;
      lineRef.current.style.setProperty("--fill", `${fillPercent}%`);
      lineRef.current.querySelector("::after"); // assicurati che width venga applicata
      lineRef.current.style.position = "relative";
      lineRef.current.querySelector("::after");
      lineRef.current.style.setProperty("width", `${fillPercent}%`);
    }
  }, [activeTab, tabs.length]);

  return (
    <div className="App">
      <div className="timetable-slider">
        <div className="line" ref={lineRef} style={{ "--fill": "0%" }}></div>

        <ul className="tabs" ref={tabsContainer}>
          {tabs.map((tab, idx) => (
            <li
              key={tab.id}
              className={activeTab === idx ? "active" : ""}
              onClick={() => setActiveTab(idx)}
            >
              <span className="dot" />
              <span className="label">{tab.label}</span>
            </li>
          ))}
        </ul>

        <div className="tab-content">
          <h2>Programma di {tabs[activeTab].label}</h2>
          <p>{tabs[activeTab].content}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
