import React, { useEffect } from "react";
import $ from "jquery";
import logo from "./logo.svg";
import "./App.css";

function App() {
  useEffect(() => {
    // safety: esce se non trova la timeline
    if (!document.querySelector(".cd-horizontal-timeline")) {
      console.warn("No .cd-horizontal-timeline element found — skipping init.");
      return;
    }

    const timelines = $(".cd-horizontal-timeline");
    const eventsMinDistance = 60;

    if (timelines.length > 0) initTimeline(timelines);

    function initTimeline(timelines) {
      timelines.each(function () {
        const timeline = $(this);
        const timelineComponents = {};

        timelineComponents.timelineWrapper = timeline.find(".events-wrapper");
        timelineComponents.eventsWrapper = timelineComponents.timelineWrapper.children(
          ".events"
        );
        timelineComponents.fillingLine = timelineComponents.eventsWrapper.children(
          ".filling-line"
        );
        timelineComponents.timelineEvents = timelineComponents.eventsWrapper.find(
          "a"
        );
        timelineComponents.timelineDates = parseDate(
          timelineComponents.timelineEvents
        );
        timelineComponents.eventsMinLapse = minLapse(
          timelineComponents.timelineDates
        );
        // fallback se c'è un solo evento / nessun evento
        if (!isFinite(timelineComponents.eventsMinLapse) || timelineComponents.eventsMinLapse <= 0) {
          timelineComponents.eventsMinLapse = 1;
        }
        timelineComponents.timelineNavigation = timeline.find(
          ".cd-timeline-navigation"
        );
        timelineComponents.eventsContent = timeline.children(".events-content");

        // posiziona eventi e imposta larghezza
        setDatePosition(timelineComponents, eventsMinDistance);
        const timelineTotWidth = setTimelineWidth(
          timelineComponents,
          eventsMinDistance
        );

        timeline.addClass("loaded");

        // NAV buttons (namespaced so we can cleanup)
        timelineComponents.timelineNavigation.on(
          "click.timeline",
          ".next",
          function (event) {
            event.preventDefault();
            updateSlide(timelineComponents, timelineTotWidth, "next");
          }
        );
        timelineComponents.timelineNavigation.on(
          "click.timeline",
          ".prev",
          function (event) {
            event.preventDefault();
            updateSlide(timelineComponents, timelineTotWidth, "prev");
          }
        );

        // clicking single event
        timelineComponents.eventsWrapper.on(
          "click.timeline",
          "a",
          function (event) {
            event.preventDefault();
            timelineComponents.timelineEvents.removeClass("selected");
            $(this).addClass("selected");
            updateOlderEvents($(this));
            updateFilling(
              $(this),
              timelineComponents.fillingLine,
              timelineTotWidth
            );
            updateVisibleContent($(this), timelineComponents.eventsContent);
          }
        );

        // simple touch swipe detection (no external libs)
        let touchStartX = null;
        timelineComponents.eventsContent.on("touchstart.timeline", function (ev) {
          const t = ev.originalEvent && ev.originalEvent.touches ? ev.originalEvent.touches[0].clientX : null;
          touchStartX = t;
        });
        timelineComponents.eventsContent.on("touchend.timeline", function (ev) {
          if (touchStartX == null) return;
          const t = ev.originalEvent && ev.originalEvent.changedTouches ? ev.originalEvent.changedTouches[0].clientX : null;
          if (t == null) { touchStartX = null; return; }
          const diff = t - touchStartX;
          if (Math.abs(diff) > 40) {
            const mq = checkMQ();
            if (mq === "mobile") {
              if (diff < 0) showNewContent(timelineComponents, timelineTotWidth, "next");
              else showNewContent(timelineComponents, timelineTotWidth, "prev");
            }
          }
          touchStartX = null;
        });

        // keyboard nav (namespaced)
        $(document).on("keyup.timeline", function (event) {
          if (event.key === "ArrowLeft" && elementInViewport(timeline.get(0))) {
            showNewContent(timelineComponents, timelineTotWidth, "prev");
          } else if (event.key === "ArrowRight" && elementInViewport(timeline.get(0))) {
            showNewContent(timelineComponents, timelineTotWidth, "next");
          }
        });
      });
    } // end initTimeline

    /* -------------------------
       Functions (unchanged logic but safer)
       ------------------------- */

    function updateSlide(timelineComponents, timelineTotWidth, string) {
      const translateValue = getTranslateValue(timelineComponents.eventsWrapper);
      const wrapperWidth = Number(
        timelineComponents.timelineWrapper.css("width").replace("px", "")
      );

      if (string === "next") {
        translateTimeline(
          timelineComponents,
          translateValue - wrapperWidth + eventsMinDistance,
          wrapperWidth - timelineTotWidth
        );
      } else {
        translateTimeline(
          timelineComponents,
          translateValue + wrapperWidth - eventsMinDistance
        );
      }
    }

    function showNewContent(timelineComponents, timelineTotWidth, string) {
      const visibleContent = timelineComponents.eventsContent.find(".selected");
      const newContent = string === "next" ? visibleContent.next() : visibleContent.prev();

      if (newContent.length > 0) {
        const selectedDate = timelineComponents.eventsWrapper.find(".selected");
        const newEvent =
          string === "next"
            ? selectedDate.parent("li").next("li").children("a")
            : selectedDate.parent("li").prev("li").children("a");

        updateFilling(newEvent, timelineComponents.fillingLine, timelineTotWidth);
        updateVisibleContent(newEvent, timelineComponents.eventsContent);
        newEvent.addClass("selected");
        selectedDate.removeClass("selected");
        updateOlderEvents(newEvent);
        updateTimelinePosition(string, newEvent, timelineComponents, timelineTotWidth);
      }
    }

    function updateTimelinePosition(string, event, timelineComponents, timelineTotWidth) {
      const eventStyle = window.getComputedStyle(event.get(0), null);
      const eventLeft = Number(eventStyle.getPropertyValue("left").replace("px", ""));
      const timelineWidth = Number(timelineComponents.timelineWrapper.css("width").replace("px", ""));
      const totalWidth = Number(timelineComponents.eventsWrapper.css("width").replace("px", ""));
      const timelineTranslate = getTranslateValue(timelineComponents.eventsWrapper);

      if ((string === "next" && eventLeft > timelineWidth - timelineTranslate) ||
          (string === "prev" && eventLeft < -timelineTranslate)) {
        translateTimeline(timelineComponents, -eventLeft + timelineWidth / 2, timelineWidth - totalWidth);
      }
    }

    function translateTimeline(timelineComponents, value, totWidth) {
      const eventsWrapper = timelineComponents.eventsWrapper.get(0);
      value = value > 0 ? 0 : value;
      value = !(typeof totWidth === "undefined") && value < totWidth ? totWidth : value;
      setTransformValue(eventsWrapper, "translateX", value + "px");

      if (value === 0) timelineComponents.timelineNavigation.find(".prev").addClass("inactive");
      else timelineComponents.timelineNavigation.find(".prev").removeClass("inactive");

      if (typeof totWidth !== "undefined" && value === totWidth) timelineComponents.timelineNavigation.find(".next").addClass("inactive");
      else timelineComponents.timelineNavigation.find(".next").removeClass("inactive");
    }

    function updateFilling(selectedEvent, filling, totWidth) {
      const eventStyle = window.getComputedStyle(selectedEvent.get(0), null);
      let eventLeft = eventStyle.getPropertyValue("left");
      const eventWidth = eventStyle.getPropertyValue("width");
      eventLeft = Number(eventLeft.replace("px", "")) + Number(eventWidth.replace("px", "")) / 2;
      const scaleValue = eventLeft / totWidth;
      setTransformValue(filling.get(0), "scaleX", scaleValue);
    }

    function setDatePosition(timelineComponents, min) {
      for (let i = 0; i < timelineComponents.timelineDates.length; i++) {
        const distance = daydiff(timelineComponents.timelineDates[0], timelineComponents.timelineDates[i]);
        const distanceNorm = Math.round(distance / timelineComponents.eventsMinLapse) + 2;
        timelineComponents.timelineEvents.eq(i).css("left", distanceNorm * min + "px");
      }
    }

    function setTimelineWidth(timelineComponents, width) {
      const timeSpan = daydiff(
        timelineComponents.timelineDates[0],
        timelineComponents.timelineDates[timelineComponents.timelineDates.length - 1]
      );
      const timeSpanNorm = Math.round(timeSpan / timelineComponents.eventsMinLapse) + 4;
      const totalWidth = timeSpanNorm * width;
      timelineComponents.eventsWrapper.css("width", totalWidth + "px");
      updateFilling(timelineComponents.timelineEvents.eq(0), timelineComponents.fillingLine, totalWidth);

      return totalWidth;
    }

    function updateVisibleContent(event, eventsContent) {
      const eventDate = event.data("date");
      const visibleContent = eventsContent.find(".selected");
      const selectedContent = eventsContent.find('[data-date="' + eventDate + '"]');
      const selectedContentHeight = selectedContent.height();

      if (selectedContent.index() > visibleContent.index()) {
        selectedContent.attr("class", "selected enter-right");
        visibleContent
          .attr("class", "leave-left")
          .one("webkitAnimationEnd oanimationend msAnimationEnd animationend", function () {
            visibleContent.removeClass("leave-left leave-right");
            selectedContent.removeClass("enter-left enter-right");
          });
      } else {
        selectedContent.attr("class", "selected enter-left");
        visibleContent
          .attr("class", "leave-right")
          .one("webkitAnimationEnd oanimationend msAnimationEnd animationend", function () {
            visibleContent.removeClass("leave-left leave-right");
            selectedContent.removeClass("enter-left enter-right");
          });
      }
      eventsContent.css("height", selectedContentHeight + "px");
    }

    function updateOlderEvents(event) {
      event.parent("li").prevAll("li").children("a").addClass("older-event").end().end().nextAll("li").children("a").removeClass("older-event");
    }

    function getTranslateValue(timeline) {
      const timelineStyle = window.getComputedStyle(timeline.get(0), null);
      let timelineTranslate =
        timelineStyle.getPropertyValue("-webkit-transform") ||
        timelineStyle.getPropertyValue("-moz-transform") ||
        timelineStyle.getPropertyValue("-ms-transform") ||
        timelineStyle.getPropertyValue("-o-transform") ||
        timelineStyle.getPropertyValue("transform") ||
        "";
      if (timelineTranslate.indexOf("(") >= 0) {
        timelineTranslate = timelineTranslate.split("(")[1];
        timelineTranslate = timelineTranslate.split(")")[0];
        timelineTranslate = timelineTranslate.split(",");
        return Number(timelineTranslate[4]);
      }
      return 0;
    }

    function setTransformValue(element, property, value) {
      if (!element || !element.style) return;
      element.style["-webkit-transform"] = property + "(" + value + ")";
      element.style["-moz-transform"] = property + "(" + value + ")";
      element.style["-ms-transform"] = property + "(" + value + ")";
      element.style["-o-transform"] = property + "(" + value + ")";
      element.style["transform"] = property + "(" + value + ")";
    }

    function parseDate(events) {
      const dateArrays = [];
      events.each(function () {
        const dateComp = $(this).data("date").split("/");
        const newDate = new Date(dateComp[2], dateComp[1] - 1, dateComp[0]);
        dateArrays.push(newDate);
      });
      return dateArrays;
    }

    // corrected: return difference in DAYS (was returning ms previously)
    function daydiff(first, second) {
      return Math.round((second - first) / (1000 * 60 * 60 * 24));
    }

    function minLapse(dates) {
      const dateDistances = [];
      for (let i = 1; i < dates.length; i++) {
        const distance = daydiff(dates[i - 1], dates[i]);
        dateDistances.push(distance);
      }
      if (dateDistances.length === 0) return 1;
      return Math.min.apply(null, dateDistances);
    }

    function elementInViewport(el) {
      let top = el.offsetTop;
      let left = el.offsetLeft;
      const width = el.offsetWidth;
      const height = el.offsetHeight;

      while (el.offsetParent) {
        el = el.offsetParent;
        top += el.offsetTop;
        left += el.offsetLeft;
      }

      return (
        top < window.pageYOffset + window.innerHeight &&
        left < window.pageXOffset + window.innerWidth &&
        top + height > window.pageYOffset &&
        left + width > window.pageXOffset
      );
    }

    function checkMQ() {
      const el = document.querySelector(".cd-horizontal-timeline");
      if (!el) return "desktop";
      const content = window
        .getComputedStyle(el, "::before")
        .getPropertyValue("content")
        .replace(/'/g, "")
        .replace(/"/g, "");
      return content || "desktop";
    }

    // cleanup on unmount: remove namespaced handlers and classes
    return () => {
      $(document).off(".timeline"); // rimuove eventi document namespaced
      $(".cd-horizontal-timeline").each(function () {
        const t = $(this);
        t.find("*").off(".timeline");
        t.off(".timeline");
        t.removeClass("loaded");
      });
    };
  }, []);

