import React, { useState } from "react";
import emailjs from "@emailjs/browser";

function FormConferma() {
  const [formData, setFormData] = useState({
    surname: "",
    name: "",
    conferma: "",
    guests: "",
    children: "",
    childrenAge: "",
    diet: "",
    night: "",
    children: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();

    // if (!formData.nome || !formData.email || !formData.messaggio) {
    //   setError("Tutti i campi sono obbligatori!");
    //   setSuccess("");
    //   return;
    // }

    setError("");

    const serviceID = "service_rxwq3ft";
    const templateID = "template_tqq6v3j";
    const publicKey = "_rofz9XCjKw5a1jZ8";

    emailjs.send(serviceID, templateID, formData, publicKey).then(
      (response) => {
        setSuccess("Email inviata correttamente!");
        setFormData({ nome: "", email: "", messaggio: "" });
      },
      (err) => setError("Errore nell'invio dell'email!")
    );
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}

      <input
        type="text"
        name="surname"
        placeholder="Cognome"
        value={formData.surname}
        onChange={handleChange}
      />
      <input
        type="text"
        name="name"
        placeholder="Nome"
        value={formData.name}
        onChange={handleChange}
      />
      Confermi la tua presenza al Matrimonio? <input class="checkbox" type="checkbox" value="1" name="conferma" />
      <input
        type="text"
        name="guests"
        placeholder="Nomi persone aggiuntive (maggiorenni)"
        value={formData.guests}
        onChange={handleChange}
      />
      <input
        type="number"
        name="children"
        placeholder="Numero di bambini"
        value={formData.children}
        onChange={handleChange}
      />
      <input
        type="number"
        name="childrenAge"
        placeholder="EtÃ  dei bambini"
        value={formData.childrenAge}
        onChange={handleChange}
      />
      <input
        type="text"
        name="diet"
        placeholder="Allergie o intolleranze alimentari"
        value={formData.diet}
        onChange={handleChange}
      />
    Sistemazione notte: <input class="checkbox" type="checkbox" value="1" name="night" />
      Facci i tuoi auguri: <textarea
        name="messaggio"
        placeholder="Messaggio"
        value={formData.messaggio}
        onChange={handleChange}
      />
      <button type="submit">Invia</button>
    </form>
  );
}

export default FormConferma;
