import React, { useState } from "react";
import "./App.css";
import FormConferma from "./components/form/FormConferma.js";
import Logo from "./img/logo.png";
import Banner from "./img/bck.png";

function App() {
  const [showForm, setShowForm] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const MENU = ["üè∞ Location", "üõå Sistemazione", "üéÅ Regali", "‚ùì FAQ"];

  return (
    <div className="App">
      <header className="header">
        {/* Hamburger Icon */}
        <div
          className={`hamburger ${mobileMenuOpen ? "open" : ""}`}
          onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
        >
          {mobileMenuOpen ? "ü•ï" : "ü•¶"} Menu
        </div>

        <div className="logo">
          <a href="#">
            <img src={Logo} alt="Logo" />
          </a>
        </div>

        {/* Desktop Menu */}
        <nav className="menu">
          {MENU.map((menuItem) => {
            // Rimuove tutto ci√≤ che non √® lettera/digit all'inizio
            const linkText = menuItem.replace(/^[^\w]+/, "").toLowerCase();
            return (
              <a
                key={menuItem}
                href={`#${linkText}`}
                onClick={() => setShowForm(false)}
              >
                {menuItem}
              </a>
            );
          })}
        </nav>

        <button className="cta-btn" onClick={() => setShowForm(true)}>
          Conferma
        </button>
      </header>

      {/* Mobile Menu */}
      {mobileMenuOpen && (
        <nav className="mobile-menu">
          {MENU.map((menuItem) => {
            const linkText = menuItem.replace(/^[^\w]+/, "").toLowerCase();
            return (
              <a
                key={menuItem}
                href={`#${linkText}`}
                onClick={() => {
                  setShowForm(false);
                  setMobileMenuOpen(false);
                }}
              >
                {menuItem}
              </a>
            );
          })}
        </nav>
      )}

      {showForm ? (
        <div className="form-wrapper">
          <FormConferma />
        </div>
      ) : (
        <>
          <div className="hero">
            <div className="hero-text">
              <h5>LIFE IS AN EVENT</h5>
              <h1>
                CREATING THE
                <br />
                BEST DAY EVER
              </h1>
              <p>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Cras
                aliquam mi id augue ultrices, in tempus elit tristique. Aliquam
                ultrices sem non.
              </p>
              <button>EXPLORE</button>
            </div>

            <div class="container">
            <img class="banner" src={Banner} alt="Banner" />
          </div>
          </div>

          <p>ciao</p>
          <p>ciao</p>
          <p>ciao</p>
          <p>ciao</p>
          <p>ciao</p>
          <p>ciao</p>
          <p>ciao</p>
          <p>ciao</p>
          <p>ciao</p>
          <p>ciao</p>
        </>
      )}
    </div>
  );
}

export default App;
