import React, { useState } from "react";
import logo from "./logo.svg";
import "./App.css";

function App() {
  const [activeTab, setActiveTab] = useState(0);

  const tabs = [
    { id: "lun", label: "Lunedì", content: "Matematica, Italiano, Scienze." },
    { id: "mar", label: "Martedì", content: "Storia, Geografia, Arte." },
    { id: "mer", label: "Mercoledì", content: "Educazione fisica, Inglese, Informatica." },
    { id: "gio", label: "Giovedì", content: "Chimica, Fisica, Musica." },
    { id: "ven", label: "Venerdì", content: "Letteratura, Disegno tecnico, Filosofia." },
  ];

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
      </header>

      <div className="timetable-slider">
        <div className="line">
          <div
            className="indicator"
            style={{
              width: `${100 / (tabs.length - 1)}%`,
              left: `${(100 / (tabs.length - 1)) * activeTab}%`,
            }}
          ></div>
        </div>

        <ul className="tabs">
          {tabs.map((tab, index) => (
            <li
              key={tab.id}
              className={activeTab === index ? "active" : ""}
              onClick={() => setActiveTab(index)}
            >
              {tab.label}
            </li>
          ))}
        </ul>

        <div className="tab-content">
          <h2>Programma di {tabs[activeTab].label}</h2>
          <p>{tabs[activeTab].content}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
