import React, { useState, useRef, useEffect } from "react";
import "./App.css";
import emailjs from "@emailjs/browser";

function App() {
  const [activeTab, setActiveTab] = useState(0);
  const lineFillRef = useRef(null);

  const tabs = [
    { 
      id: "location", 
      label: "Location", 
      content: (
        <div>
          <p>Indirizzo: Via Roma 123</p>
          <iframe
            title="mappa"
            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2883.687089237203!2d10.999999!3d45.438611!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x0!2zNDUuNDM4NjExLCDIuOTk5OTk!5e0!3m2!1sit!2sit!4v1600000000000!5m2!1sit!2sit"
            width="100%"
            height="200"
            style={{ border: 0 }}
            allowFullScreen=""
            loading="lazy"
          ></iframe>
        </div>
      ),
    },
    { 
      id: "sistemazione", 
      label: "Sistemazione", 
      content: (
        <div>
          <h3>Hotel consigliati</h3>
          <ul>
            <li>Hotel Bella Vista</li>
            <li>Residence Verde</li>
            <li>B&B Centrale</li>
          </ul>
        </div>
      ),
    },
    { 
      id: "programma", 
      label: "Programma", 
      content: (
        <div>
          <h3>Giornata tipo</h3>
          <ol>
            <li>09:00 - Colazione</li>
            <li>10:00 - Attivit√†</li>
            <li>13:00 - Pranzo</li>
            <li>15:00 - Escursione</li>
          </ol>
        </div>
      ),
    },
    { 
      id: "regali", 
      label: "Regali", 
      content: (
        <div>
          <p>Idea regalo per gli ospiti:</p>
          <ul>
            <li>Scatola di cioccolatini</li>
            <li>Piccolo souvenir</li>
            <li>Foto personalizzata</li>
          </ul>
        </div>
      ),
    },
    { 
      id: "conferma", 
      label: "Conferma", 
      content: (
        <div>
          <p>Per confermare la partecipazione, compila il modulo:</p>
          <form>
            <input type="text" placeholder="Nome" />
            <input type="email" placeholder="Email" />
            <button type="submit">Invia</button>
          </form>
        </div>
      ),
    },
  ];

  useEffect(() => {
    if (lineFillRef.current) {
      const fillPercent =
        tabs.length > 1 ? (activeTab / (tabs.length - 1)) * 100 : 0;
      lineFillRef.current.style.width = `${fillPercent}%`;
    }
  }, [activeTab, tabs.length]);

  function FormConferma() {
    const [formData, setFormData] = useState({
      nome: "",
      email: "",
      messaggio: ""
    });
    const [error, setError] = useState("");
    const [success, setSuccess] = useState("");

    const handleChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
      e.preventDefault();

      // Validazione campi obbligatori
      if (!formData.nome || !formData.email || !formData.messaggio) {
        setError("Tutti i campi sono obbligatori!");
        setSuccess("");
        return;
      }

      setError("");

      // Configura EmailJS
      const serviceID = "service_bjalwwv";
      const templateID = "YOUR_TEMPLATE_ID";
      const publicKey = "YOUR_PUBLIC_KEY";

      emailjs
        .send(serviceID, templateID, formData, publicKey)
        .then(
          (response) => {
            console.log("SUCCESS!", response.status, response.text);
            setSuccess("Email inviata correttamente!");
            setFormData({ nome: "", email: "", messaggio: "" });
          },
          (err) => {
            console.log("FAILED...", err);
            setError("Errore nell'invio dell'email!");
          }
        );
    };}

  return (
    <div className="App">
      <div className="timetable-slider">
        <div className="line">
          <div className="line-fill" ref={lineFillRef}></div>
        </div>

        <ul className="tabs">
          {tabs.map((tab, idx) => (
            <li
              key={tab.id}
              className={activeTab === idx ? "active" : ""}
              onClick={() => setActiveTab(idx)}
            >
              <span className="dot" />
              <span className="label">{tab.label}</span>
            </li>
          ))}
        </ul>

        <div className="tab-content">
          {tabs[activeTab].content}
        </div>
      </div>
      <form onSubmit={handleSubmit}>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}

      <input
        type="text"
        name="nome"
        placeholder="Nome"
        value={formData.nome}
        onChange={handleChange}
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
      />
      <textarea
        name="messaggio"
        placeholder="Messaggio"
        value={formData.messaggio}
        onChange={handleChange}
      />
      <button type="submit">Invia</button>
    </form>
    </div>
  );
}

export default App;
