import React, { useState } from "react";
import logo from "./logo.svg";
import "./App.css";

function App() {
  const [activeTab, setActiveTab] = useState("lun");

  const tabs = [
    { id: "lun", label: "Lunedì", content: "Matematica, Italiano, Scienze." },
    { id: "mar", label: "Martedì", content: "Storia, Geografia, Arte." },
    { id: "mer", label: "Mercoledì", content: "Educazione fisica, Inglese, Informatica." },
    { id: "gio", label: "Giovedì", content: "Chimica, Fisica, Musica." },
    { id: "ven", label: "Venerdì", content: "Letteratura, Disegno tecnico, Filosofia." },
  ];

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
      </header>

      <div className="timetable">
        <ul className="tabs">
          {tabs.map((tab) => (
            <li
              key={tab.id}
              className={activeTab === tab.id ? "active" : ""}
              onClick={() => setActiveTab(tab.id)}
            >
              {tab.label}
            </li>
          ))}
        </ul>

        {tabs.map((tab) => (
          <div
            key={tab.id}
            id={tab.id}
            className={`tab-content ${activeTab === tab.id ? "active" : ""}`}
          >
            <h2>Programma di {tab.label}</h2>
            <p>{tab.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
