import React, { useState } from "react";
import "./App.css";
import FormConferma from "./components/form/FormConferma.js";
import Logo from "./img/logo.png";

function App() {
  const [showForm, setShowForm] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const MENU = ["Location", "Sistemazione", "Regali", "FAQ"];
  return (
    <div class="App">
      <header class="header">
        <div class="logo">
          <img src={Logo} alt="Logo" />
        </div>
        {/* Desktop Menu */}
        <div class="menu">
          {MENU.map((menuItem) => {
            return <a href={`#${menuItem.toLowerCase()}`} onClick={() => setShowForm(false)}>{menuItem}</a>;
          })}
        </div>
        <a href={"#"} onClick={() => setShowForm(false)}><button class="cta-btn" onClick={() => setShowForm(true)}>Conferma</button></a>
      {/* Hamburger Icon */}
      <div
          className={`hamburger ${mobileMenuOpen ? "open" : ""}`}
          onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
        >
          <span></span>
          <span></span>
          <span></span>
        </div>

        <button className="cta-btn" onClick={() => setShowForm(true)}>
          Conferma
        </button>
      </header>

      {/* Mobile Menu */}
      {mobileMenuOpen && (
        <nav className="mobile-menu">
          {MENU.map((menuItem) => (
            <a
              key={menuItem}
              href={`#${menuItem.toLowerCase()}`}
              onClick={() => {
                setShowForm(false);
                setMobileMenuOpen(false);
              }}
            >
              {menuItem}
            </a>
          ))}
        </nav>
      )}
      {showForm && (
        <div className="form-wrapper">
          <FormConferma />
        </div>
      )}
    </div>
  );
}

export default App;
