import React, { useEffect } from "react";
import $ from "jquery";
import logo from "./logo.svg";
import "./App.css";

function App() {
  document.addEventListener("DOMContentLoaded", () => {
    const tabs = document.querySelectorAll(".tabs li");
    const contents = document.querySelectorAll(".tab-content");

    tabs.forEach(tab => {
      tab.addEventListener("click", () => {
        // rimuovo active da tutte le tab
        tabs.forEach(t => t.classList.remove("active"));
        contents.forEach(c => c.classList.remove("active"));

        // aggiungo active a quella cliccata
        tab.classList.add("active");
        document.getElementById(tab.dataset.tab).classList.add("active");
      });
    });
  });
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
      </header>

      <div class="timetable">
    <ul class="tabs">
      <li data-tab="lun" class="active">Lunedì</li>
      <li data-tab="mar">Martedì</li>
      <li data-tab="mer">Mercoledì</li>
      <li data-tab="gio">Giovedì</li>
      <li data-tab="ven">Venerdì</li>
    </ul>

    <div class="tab-content active" id="lun">
      <h2>Programma di Lunedì</h2>
      <p>Matematica, Italiano, Scienze.</p>
    </div>
    <div class="tab-content" id="mar">
      <h2>Programma di Martedì</h2>
      <p>Storia, Geografia, Arte.</p>
    </div>
    <div class="tab-content" id="mer">
      <h2>Programma di Mercoledì</h2>
      <p>Educazione fisica, Inglese, Informatica.</p>
    </div>
    <div class="tab-content" id="gio">
      <h2>Programma di Giovedì</h2>
      <p>Chimica, Fisica, Musica.</p>
    </div>
    <div class="tab-content" id="ven">
      <h2>Programma di Venerdì</h2>
      <p>Letteratura, Disegno tecnico, Filosofia.</p>
    </div>
  </div>
    </div>
  );
}

export default App;
