import React, { useState } from "react";
import emailjs from "@emailjs/browser";

function FormConferma() {
  const [formData, setFormData] = useState({
    name: "",
    conferma: "",
    guests: "",
    children: "",
    childrenAge: "",
    diet: "",
    night: "",
    msg: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validazione form
    if (!formData.name || !formData.conferma) {
      setError("Nome e conferma sono obbligatori!");
      setSuccess("");
      return;
    }

    setError("");

    const serviceID = "service_rxwq3ft";
    const templateID = "template_tqq6v3j";
    const publicKey = "_rofz9XCjKw5a1jZ8";

    emailjs.send(serviceID, templateID, formData, publicKey).then(
      () => {
        setSuccess("Email inviata correttamente!");
        setFormData({
          name: "",
          conferma: "",
          guests: "",
          children: "",
          childrenAge: "",
          diet: "",
          night: "",
          msg: "",
        });
      },
      () => setError("Errore nell'invio dell'email!")
    );
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}
      <label>
        üë§ Chi sei tu, meraviglioso invitato? Nome e cognome:
        <span className="desc">Cos√¨ possiamo scriverlo sul segnaposto e non chiamarti ‚Äúquello simpatico con gli occhiali‚Äù.</span>
        <input
          type="text"
          name="name"
          placeholder="Nome e Cognome"
          value={formData.name}
          onChange={handleChange}
        />
      </label>
      <label>
        Farai parte della festa pi√π bella dell‚Äôanno (modestamente parlando)?
      </label>
      <label className="radioBtn">
        <input
          type="radio"
          name="conferma"
          value="S√¨"
          checked={formData.conferma === "S√¨"}
          onChange={handleChange}
        />
        üéä Eccome se ci sar√≤!
      </label>
      <label className="radioBtn">
        <input
          type="radio"
          name="conferma"
          value="No"
          checked={formData.conferma === "No"}
          onChange={handleChange}
        />
        üò¢ Ahim√®, devo declinare l‚Äôinvito
      </label>
      <label>
        üßë‚Äçü§ù‚Äçüßë Viene qualcun altro con te? Chi?
        <span className="desc">Scrivi i nomi, per prepararci a riceverli con un sorriso (e il posto a tavola giusto!).</span>
        <input
          type="text"
          name="guests"
          placeholder="Inserisci..."
          value={formData.guests}
          onChange={handleChange}
        />
      </label>
      <label>
        üßí Hai mini-esseri umani al seguito?
        <input
          type="text"
          name="children"
          placeholder="Vogliamo accoglierli al meglio, con sorrisi e magari qualche palloncino."
          value={formData.children}
          onChange={handleChange}
        />
      </label>
      <label>
        üßíüßí Et√† dei bambini:
        <input
          type="number"
          name="childrenAge"
          placeholder="Cos√¨ non prepariamo il men√π baby a tuo figlio adolescente."
          value={formData.childrenAge}
          onChange={handleChange}
        />
      </label>
      <label>
        ü•¶ Allergie, intolleranze o ingredienti da evitare? Il men√π sar√† 100% vegano e vegetariano, ma niente paura: sar√† cos√¨ buono che anche l‚Äô amante della griglia chieder√† il bis!
        <input
          type="text"
          name="diet"
          placeholder="Segnalaci tutto ci√≤ che dobbiamo sapere per farti mangiare felice e sereno."
          value={formData.diet}
          onChange={handleChange}
        />
      </label>
      <label>
        üè® Notte del 10/11 Ottobre ‚Äì Serve un tetto?
        <label>Hai bisogno di pernottare? Per quante persone?</label>
        <input
          type="text"
          name="night"
          placeholder="(Pensato per chi arriva dalla Lombardia, Piemonte, Austria o altre terre lontane‚Ä¶)"
          value={formData.night}
          onChange={handleChange}
        />
      </label>
      üí¨Hai un messaggio per noi, una richiesta speciale, un pensiero buffo o
      qualcosa che dovremmo includere nel nostro programma del matrimonio (anzi,
      unione civile!!!!)?
