      <textarea
        name="msg"
        placeholder="Scrivi pure tutto quello che ti passa per la testa (quasi tutto üòÑ). "
        value={formData.msg}
        onChange={handleChange}
      />
      <button type="submit">Invia</button>
    </form>
  );
}

export default FormConferma;
