import React, { useState, useRef, useEffect } from "react";
import "./App.css";

function App() {
  const [activeTab, setActiveTab] = useState(0);
  const lineFillRef = useRef(null);

  const tabs = [
    { id: "location", label: "Location", content: "Matematica, Italiano, Scienze." },
    { id: "sistemazione", label: "Sistemazione", content: "Storia, Geografia, Arte." },
    {
      id: "programma",
      label: "Programma",
      content: "Educazione fisica, Inglese, Informatica.",
    },
    { id: "regali", label: "Regali", content: "Chimica, Fisica, Musica." },
    {
      id: "conferma",
      label: "Conferma",
      content: "Letteratura, Disegno tecnico, Filosofia.",
    },
  ];

  useEffect(() => {
    if (lineFillRef.current) {
      const fillPercent =
        tabs.length > 1 ? (activeTab / (tabs.length - 1)) * 100 : 0;
      lineFillRef.current.style.width = `${fillPercent}%`;
    }
  }, [activeTab, tabs.length]);

  return (
    <div className="App">
      <div className="timetable-slider">
        <div className="line">
          <div className="line-fill" ref={lineFillRef}></div>
        </div>

        <ul className="tabs">
          {tabs.map((tab, idx) => (
            <li
              key={idx}
              className={activeTab === idx ? "active" : ""}
              onClick={() => setActiveTab(idx)}
            >
              <span className="dot" />
              <span className="label">{tab.label}</span>
            </li>
          ))}
        </ul>

        <div className="tab-content">
          <h2>{tabs[activeTab].label}</h2>
          <p>{tabs[activeTab].content}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
