import React, { useState } from "react";
import "./App.css";
import FormConferma from "./components/form/FormConferma.js";
import Logo from "./img/logo.png";

function App() {
  const [showForm, setShowForm] = useState(false);

  const MENU = ["Location", "Sistemazione", "Regali", "FAQ"];
  return (
    <div class="App">
      <header class="header">
        <div class="logo">
          <img src={Logo} alt="Logo" />
        </div>
        <div class="menu">
          {MENU.map((menuItem) => {
            return <a href={`#${menuItem.toLowerCase()}`} onClick={() => setShowForm(false)}>{menuItem}</a>;
          })}
        </div>
        <button class="cta-btn" onClick={() => setShowForm(true)}>Conferma</button>
      </header>
      {showForm && (
        // <div className="form-wrapper">
          <FormConferma />
        // </div>
      )}
    </div>
  );
}

export default App;
